def team_repos_query(org, team_slug, repos_cursor=""):
  return f'{{"query": "query Team_Repos(\\n$teamSlug: String!\\n$org: String!\\n$reposCursor: String\\n$unwantedBranchName: String!\\n$unwantedBranchQualifiedName: String!\\n) {{\\norganization(login: $org) {{\\nid\\nname\\nteam(slug: $teamSlug) {{\\nname\\nrepositories(first: 100, after: $reposCursor) {{\\ntotalCount\\npageInfo {{\\nendCursor\\nhasNextPage\\n}}\\nedges {{\\npermission\\nnode {{\\nisArchived\\nid\\nname\\ndefaultBranchRef {{\\nid\\nname\\n}}\\nbranchProtectionRules(first: 5) {{\\nedges {{\\nnode {{\\nrequiredStatusCheckContexts\\nmatchingRefs(first: 5) {{\\ntotalCount\\nnodes {{\\nname\\n}}\\n}}\\n}}\\n}}\\n}}\\npullRequests(baseRefName: $unwantedBranchName, states: [OPEN]) {{\\ntotalCount\\n}}\\nref(qualifiedName: $unwantedBranchQualifiedName) {{\\nname\\n}}\\n}}\\n}}\\n}}\\n}}\\n}}\\n}}", "variables": {{"teamSlug": "{team_slug}", "org": "{org}", "reposCursor": "{repos_cursor}", "unwantedBranchName": "master", "unwantedBranchQualifiedName": "refs/heads/master"}}, "operationName": null}}'.encode('utf-8')

def team_names_query(org, teams_cursor=""):
  return f'{{"query": "query Teams($org: String!{", $teamsCursor: String" if teams_cursor else ""}) {{\\norganization(login: $org) {{\\nname\\nteams(first: 100{", after: $teamsCursor" if teams_cursor else ""}) {{\\ntotalCount\\npageInfo {{\\nhasNextPage\\nendCursor\\n}}\\nnodes {{\\nslug\\n}}\\n}}\\n}}\\n}}", "variables": {{"org": "{org}", "teamsCursor": "{teams_cursor}"}}, "operationName": null}}'.encode('utf-8')

def team_repo_name_query(org, team_slug, repos_cursor=""):
  return f'{{"query": "query Per_Team_Repos($org: String!, $teamSlug: String!, $reposCursor: String) {{\\norganization(login: $org) {{\\nname\\nteam(slug: $teamSlug) {{\\nname\\nrepositories(first: 100, after: $reposCursor) {{\\ntotalCount\\npageInfo {{\\nendCursor\\nhasNextPage\\n}}\\nedges {{\\npermission\\nnode {{\\nisArchived\\nname\\n}}\\n}}\\n}}\\n}}\\n}}\\n}}", "variables": {{"org": "{org}", "teamSlug": "{team_slug}", "reposCursor": "{repos_cursor}"}}, "operationName": null}}'.encode('utf-8')
